package week1.day2;

public class FindEvenorOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=53;
				if(number%2!=0)
				{
					System.out.println("The number is odd");
				}
				else
				{
					System.out.println("The number is even");
				}

	}

}
